mapper.update:
update member
set ust='Y'
where uemail = #{uemail} and accesstoken = #{accesstoken}

update member
set uname = #{uname}, uphone = #{uphone}, ugroup = #{ugroup}, urole =
#{urole}
where ucode = #{ucode}

update member
set uimg = #{uimg}
where ucode = #{ucode}

update member
set upwd = #{upwd}
where ucode = #{ucode}

update member
set upwd = #{upwd}
where uemail = #{uemail} and signtype= 'h'

update pjmember
set pjadmin= #{pjadmin}
where pnum=#{pnum} and ucode = #{ucode}

mapper.select:

select * from pjmember
left join member using (ucode)
where pnum = #{pnum}
